package com.tal.android.pingpong.ui.mvp.model

import com.google.android.gms.auth.api.signin.GoogleSignInAccount
import com.nerdscorner.mvplib.events.model.BaseEventsModel
import com.tal.android.pingpong.domain.Challenge
import com.tal.android.pingpong.domain.MatchRecord
import com.tal.android.pingpong.domain.User
import com.tal.android.pingpong.extensions.enqueueResponseNotNull
import com.tal.android.pingpong.networking.ServiceGenerator
import com.tal.android.pingpong.networking.services.ChallengesService
import java.util.*

class ChallengesModel(private val googleUser: GoogleSignInAccount?) : BaseEventsModel() {

    private val challengesService = ServiceGenerator.createService(ChallengesService::class.java)

    fun fetchUserChallenges() {
        challengesService
            .getMyChallenges()
            .enqueueResponseNotNull(
                success = {
                    bus.post(ChallengesFetchedSuccessfullyEvent(it))
                },
                fail = {
                    bus.post(ChallengesFetchFailedEvent())
                }
            )
        val diego = User(
            "",
            "Diego Marcher",
            "dmarcher@theappraisallane.com",
            "data:image/jpeg;base64,"
        )
        val javieeeer = User(
            "",
            "Javier Gonz√°lez",
            "jogonzalez@theappraisallane.com",
            "data:image/jpeg;base64,"
        )
        bus.post(ChallengesFetchedSuccessfullyEvent(mutableListOf<Challenge>().apply {
            add(Challenge().apply {
                challengeMatch = MatchRecord().apply {
                    matchDate = Date()
                    local = diego
                    visitor = javieeeer
                }
            })
            add(Challenge().apply {
                challengeMatch = MatchRecord().apply {
                    matchDate = Date()
                    visitor = diego
                    local = javieeeer
                    matchesHistory = mutableListOf<MatchRecord>().apply {
                        add(MatchRecord().apply {
                            local = diego
                            visitor = javieeeer
                            matchDate = Date()
                            localScore = 21
                            visitorScore = 15
                        })
                        add(MatchRecord().apply {
                            local = diego
                            visitor = javieeeer
                            matchDate = Date()
                            localScore = 14
                            visitorScore = 21
                        })
                        add(MatchRecord().apply {
                            local = diego
                            visitor = javieeeer
                            matchDate = Date()
                            localScore = 0
                            visitorScore = 15
                        })
                    }
                }
            })
            add(Challenge().apply {
                challengeMatch = MatchRecord().apply {
                    matchDate = Date()
                    local = diego
                    visitor = javieeeer
                }
            })
        }))
    }

    fun getUserEmail() = googleUser?.email

    class ChallengesFetchedSuccessfullyEvent(val challenges: List<Challenge>)
    class ChallengesFetchFailedEvent
}
